@dp.message_handler(text=['Бот', 'бот', 'Чапча', 'чапча'])
async def process_giveeclairs_command(message: types.Message):
    ra = randint(1,7)
    if ra == 1:
        await message.answer("✅ На посту!")
    elif ra == 2:
        await message.answer("✅ Уже тут!")
    elif ra == 3:
        await message.answer("✅ Да тут я!")
    elif ra == 4:
        await message.answer("✅ Я все еще здесь!")
    elif ra == 5:
        await message.answer("✅ Еще не ушел!")
    elif ra == 6:
        await message.answer("✅ К Вашим услугам!")
    else:
        if message.text == "бот" or message.text == "Бот":
            await message.answer("✅ Фриби здесь!")
        else:
            await message.answer("✅ Да-да?")

