    db = sqlite3.connect('chapcha_data_base.db')
    c = db.cursor()
    c.execute("SELECT user_id FROM user_data")
    items = c.fetchall()
    us = 0
    for el in items:
        for e in el:
            us += 1
    await message.answer(f"""В чапче зарегистрировано <b>{us} пользователей</b> считая разработчика""", parse_mode="HTML")
